(function(){"use strict";var scripts=document.getElementsByTagName("script");var currentScriptPath=scripts[scripts.length-1].src;if(currentScriptPath.length==0){currentScriptPath="./ac-angular-uploads/includes/upload.php"}angular.module("acUploads",[]).directive("acUploadFiles",AcUploadFiles).factory("UploadService",UploadService).service("UploadVars",UploadVars);UploadService.$inject=["UploadVars"];function UploadService(UploadVars){var services={};services.uploadImages=uploadImages;services.addImages=addImages;return services;function addImages(filelist,id,sub_folder,callback){for(var i=0;i<filelist.length;++i){var file=filelist.item(i);uploadImages(file,sub_folder,callback);var encontrado=-1;for(var x=0;x<UploadVars.uploadsList.length;x++){if(UploadVars.uploadsList[x].id==id){encontrado=x}}if(encontrado>-1){UploadVars.uploadsList[encontrado].file=file}else{UploadVars.uploadsList.push({id:id,file:file})}}}function uploadImages(file,sub_folder,callback){var form_data=new FormData;if(file.size>4e5){alert("No se puede subir un archivo que sea mayor a 400k");return}form_data.append("images",file);form_data.append("folder",sub_folder);var ajax=new XMLHttpRequest;ajax.onprogress=function(){};ajax.onload=function(data){callback(data)};ajax.onerror=function(data){callback(data)};ajax.open("POST",currentScriptPath);ajax.send(form_data)}}function AcUploadFiles(){return{restrict:"A",scope:{acUploadFiles:"&"},link:function(scope,element,attr,Controller){element.bind("change",function(){scope.acUploadFiles({selectedFileList:element[0].files})})}}}function UploadVars(){this.uploadsList=[]}})();